package practice.java.javaPkgOne;

public class JavaClassOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Java Selenium");
		//Integer Data Type
		int age = 30; //Age is a variable of type integer which holds value 30, " = " is assignment operator , assign from right to left
		System.out.println(age);
		double d = 1000.10; //Double holds numeric and decimal data
		char c = 'c'; //character always holds single character, if we try to enter more than one character then error msg will be displayed
		boolean b = true; //boolean always holds only true and false, by default boolean value is false
		System.out.println(10>2);// >,<,>=,<=,!=,== are conditional operators 
	}
	//public static void Main(String[] args) {} : this is a separate as Java is a Case Sensitive
}
